package model.entity;import java.sql.CallableStatement;import java.sql.ResultSet;import java.sql.SQLException;//import java.text.ParseException;//import java.text.SimpleDateFormat;//import java.util.Date;import model.DatabaseConnector;/** * * @author Littl */public class Member extends User {    protected String contact = "example@member.ppvs";    protected String dateOfBirth = "2005-01-01";    public Member(String ID, String firstName, String lastName) {        this.ID = ID;        this.firstName = firstName;        this.lastName = lastName;    }        /**     * default Full-information Constructor for Member.     *      * @param ID = memberID (MSV)     * @param firstName = first name     * @param lastName = last name     * @param contact = email/ phone/...     * @param dateOfBirth = format "YYYY-MM-DD"     */    public Member(String ID, String firstName, String lastName, String contact, String dateOfBirth) {        this.ID = ID;        this.firstName = firstName;        this.lastName = lastName;        this.contact = contact;        this.dateOfBirth = dateOfBirth;            }        /**     * Load FULL INFORMATION to this Member through ID.     *      * @param ID = ID người dùng     *      * @throws SQLException      */    public Member(String ID) throws SQLException {        CallableStatement finder = (CallableStatement) DatabaseConnector.getConnection().prepareCall("{ call getMemberInfo(?) }");                finder.setString(1, ID);                ResultSet rs;        rs = finder.executeQuery();                while (rs.next()) {            this.ID = rs.getString(1);            firstName = rs.getString(2);            lastName = rs.getString(3);            contact = rs.getString(4);            dateOfBirth = rs.getString(5);        }    }        public Member(Member org) {        this.ID = org.ID;        this.firstName = org.firstName;        this.lastName = org.lastName;        this.contact = org.contact;        this.dateOfBirth = org.dateOfBirth;    }        public String getContact() {        return contact;    }    public void setContact(String contact) {        this.contact = contact;    }//    public Date getDateOfBirth() {//        return dateOfBirth;//    }        public String getDateOfBirth() {        return dateOfBirth;    }    public void setDateOfBirth(String dateOfBirth) {        this.dateOfBirth = dateOfBirth;    }    @Override    public String toString() {        return "Member{ID="+ ID + ", firstName=" + firstName + ", lastName=" + lastName + ", contact=" + contact + ", dateOfBirth=" + getDateOfBirth() + '}';    }        /**     * convert to java.util.Date from sql date format     *      * @param date = format "YYYY-MM-DD"     *      * @return the true date     *///    static Date convertStringToDate(String date) {//        System.out.println(date);//        SimpleDateFormat formatter = new SimpleDateFormat("yyyy-MM-dd");////        try {//            // Chuyển đổi chuỗi thành java.util.Date//            return formatter.parse(date);//            //        } catch (ParseException e) {//            System.out.println("Định dạng chuỗi không hợp lệ: " + e.getMessage());//            System.out.println(date);//        }//        //        return null;//    }}