package model.entity;import java.sql.CallableStatement;import java.sql.ResultSet;import java.sql.SQLException;//import java.text.ParseException;//import java.text.SimpleDateFormat;//import java.util.Date;import model.DatabaseConnector;/** * * @author Littl */public class Member extends User {    protected String contact = "example@member.ppvs";    protected String dateOfBirth = "2005-01-01";    /**     * Constructor rút gọn. Để test.     *      * @param ID     * @param firstName     * @param lastName      */    public Member(String ID, String firstName, String lastName) {        this.ID = ID;        this.firstName = firstName;        this.lastName = lastName;    }        /**     * Contructor khởi tạo người dùng (Member) mới     *      * @param ID = memberID (MSV)     * @param firstName = first name     * @param lastName = last name     * @param contact = email/ phone/...     * @param dateOfBirth = format "YYYY-MM-DD"     */    public Member(String ID, String firstName, String lastName, String contact, String dateOfBirth) {        this.ID = ID;        this.firstName = firstName;        this.lastName = lastName;        this.contact = contact;        this.dateOfBirth = dateOfBirth;            }        /**     * Lấy thông tin người dùng từ database thông qua UserID     *      * @param ID = ID người dùng     *      * @throws SQLException      */    public Member(String ID) throws SQLException {        CallableStatement finder = (CallableStatement) DatabaseConnector.getConnection().prepareCall("{ call getMemberInfo(?) }");                finder.setString(1, ID);                ResultSet rs;        rs = finder.executeQuery();                while (rs.next()) {            this.ID = rs.getString(1);            firstName = rs.getString(2);            lastName = rs.getString(3);            contact = rs.getString(4);            dateOfBirth = rs.getString(5);        }    }        /**     * Clone thông tin của người dùng.     *      * @param org = mẫu gốc.     */    public Member(Member org) {        this.ID = org.ID;        this.firstName = org.firstName;        this.lastName = org.lastName;        this.contact = org.contact;        this.dateOfBirth = org.dateOfBirth;    }        @Override    public String getContact() {        return contact;    }    @Override    public void setContact(String contact) {        this.contact = contact;    }        public String getDateOfBirth() {        return dateOfBirth;    }    public void setDateOfBirth(String dateOfBirth) {        this.dateOfBirth = dateOfBirth;    }    @Override    public String toString() {        return "Member{ID="+ ID + ", firstName=" + firstName + ", lastName=" + lastName + ", contact=" + contact + ", dateOfBirth=" + getDateOfBirth() + '}';    }}